package week1.day2;

public class IfElseStatements {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int mark=360;


		if(mark>400)
		{
			System.out.println("Mark scored is Excellent");
		}
		else if(mark>320)
		{
			System.out.println("Mark scored is Average");
		}
		else {
			System.out.println("Mark scored is below average");
		}

	}
}
